<?php

use MotaMonteiro\Helpers\Traits\StringHelper;
use PHPUnit\Framework\TestCase;

class ImprimirSrcImagemTest extends TestCase
{
    use StringHelper;

    public function testImprimirSrcImagemSucesso()
    {
        $src = $this->imprimirSrcImagem('image/jpeg', '');
        $img = '<img src="'.$src.'" width="100">';
        $imgEsperado = '<img src="data:image/jpeg;base64," width="100">';
        $this->assertEquals($img, $imgEsperado);
    }
}